	SELECT [CustomerId], [LastName], [FirstName], [Email] FROM [Customer] WHERE [Email] = @Email and PAsswd = HASHBYTES('SHA2_512', dbo.MVSF_GetPreHash() + @Passwd + dbo.MVSF_GetPostHash());
END

BEGIN
	INSERT INTO [Customer] (LastName, FirstName, Email, Passwd) VALUES (@LastName, @FirstName, @Email, HASHBYTES('SHA2_512', dbo.MVSF_GetPreHash() + @Passwd + dbo.MVSF_GetPostHash()));
END

